$(".likes").click(function (){
    if($(this).hasClass('done')){
        layer.msg('强撸灰飞烟灭吖~');
        return false;
    }else{
        $(this).addClass('done');
        var id = $(this).data("id"),
            action = $(this).data('action'),
            rateHolder = $(this).children('.count');
        var ajax_data = {
            action: "ycsd_like",
            um_id: id,
            um_action: action
        };
        $.post("/wp-admin/admin-ajax.php", ajax_data, function (data){
            $(rateHolder).html(data);
        });
        return false;
    }
});
