$(function (){
    $(window).scroll(function (){
        if($(this).scrollTop() > 100){
            $('.go_top').show(300);
        }else{
            $('.go_top').hide(300);
        }
    });
    $(".go_top").click(function (){
        $("html, body").animate({
            scrollTop: 0
        }, 600);
        return false
    })
});
